mapper.select:
SELECT COUNT(*)
  FROM csat_community
 WHERE community_seq = #{communitySequence}
   AND student_seq = #{studentSequence}

mapper.update:
UPDATE csat_community
   SET content = #{content}
 WEHRE community_seq = #{communitySequence}

UPDATE csat_community
   SET use_yn = 'N'
 WHERE community_seq = #{communitySequence}
