mapper.select:
SELECT COUNT(*)
  FROM csat_community_comment
 WHERE community_seq = #{communitySequence}
   AND comment_seq = #{commentSequence}
   AND student_seq = #{studentSequence}

mapper.update:
UPDATE csat_community_comment
   SET content = #{content}
 WHERE community_seq = #{communitySequence}
   AND comment_seq = #{commentSequence}
   AND use_yn = 'Y'

UPDATE csat_community_comment
   SET use_yn = 'N'
 WHERE community_seq = #{communitySequence}
   AND comment_seq = #{commentSequence}
