mapper.select:
SELECT COUNT(*)
  FROM csat_exam_avg
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}

SELECT TRUNCATE(( ( #{score} - cea.avg ) / cea.standard_deviation ) * ( 
                ces.max_score / 5 ) 
                       + 
                       ces.max_score, 0) AS standard_score 
  FROM csat_exam_avg cea,csat_exam_subject ces 
 WHERE cea.csat_seq = #{csatSequence}
   AND cea.exam_seq = #{examSequence}
   AND cea.section_seq = #{sectionSequence}
   AND cea.subject_seq = #{subjectSequence}
   AND cea.csat_seq = ces.csat_seq 
   AND cea.exam_seq = ces.exam_seq 
   AND cea.section_seq = ces.section_seq 
   AND cea.subject_seq = ces.subject_seq

mapper.update:
UPDATE csat_exam_avg SET avg = #{average}, standard_deviation = #{standardDeviation}
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}

mapper.delete:
DELETE FROM csat_exam_avg
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}

   		AND section_seq = #{sectionSequence}


   		AND subject_seq = #{subjectSequence}

DELETE FROM csat_exam_avg 
 WHERE csat_seq IN  

		#{list.csatSequence}

   AND exam_seq IN 

    	#{list.examSequence}

   AND !(

   		(section_seq = #{list.sectionSequence} AND subject_seq = #{list.subjectSequence})
   	)

DELETE cea FROM csat_exam_avg cea , (SELECT csat_seq,exam_seq,section_seq,subject_seq FROM csat_exam_subject WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence} AND subject_seq = #{list.subjectSequence}) )) ces WHERE cea.csat_seq = ces.csat_seq AND cea.exam_seq = ces.exam_seq AND cea.section_seq = ces.section_seq AND cea.subject_seq = ces.subject_seq

DELETE cea FROM csat_exam_avg cea , (SELECT csat_seq,exam_seq,section_seq FROM csat_exam_section WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence}) )) ces WHERE cea.csat_seq = ces.csat_seq AND cea.exam_seq = ces.exam_seq AND cea.section_seq = ces.section_seq
