mapper.select:
SELECT csat_seq,exam_seq,section_seq,subject_seq,text_seq,description 
  FROM csat_qst_text 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
   AND begin_qst_seq <= #{questionSequence}
   AND end_qst_seq >= #{questionSequence}

SELECT text_seq,begin_qst_seq,end_qst_seq,description 
  FROM csat_qst_text 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
 ORDER BY text_seq ASC

mapper.delete:
DELETE FROM csat_qst_text 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}

   		AND section_seq = #{sectionSequence}


   		AND subject_seq = #{subjectSequence}

DELETE cqt FROM csat_qst_text cqt , (SELECT csat_seq,exam_seq,section_seq,subject_seq FROM csat_exam_subject WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence} AND subject_seq = #{list.subjectSequence}) )) ces WHERE cqt.csat_seq = ces.csat_seq AND cqt.exam_seq = ces.exam_seq AND cqt.section_seq = ces.section_seq AND cqt.subject_seq = ces.subject_seq

DELETE cqt FROM csat_qst_text cqt , (SELECT csat_seq,exam_seq,section_seq FROM csat_exam_section WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence}) )) ces WHERE cqt.csat_seq = ces.csat_seq AND cqt.exam_seq = ces.exam_seq AND cqt.section_seq = ces.section_seq
