mapper.select:
SELECT COUNT(*) 
  FROM csat_qst_objtitem_img 
 WHERE csat_seq = #{csatSequence} 
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
   AND qst_seq = #{questionSequence}
   AND objtitem_seq = #{objectItemSequence}
   AND img_seq = #{imageSequence}

SELECT file_name 
  FROM csat_qst_objtitem_img 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
   AND qst_seq = #{questionSequence}
   AND objtitem_seq = #{objectItemSequence}
   AND img_seq = #{imageSequence}

SELECT file_code 
  FROM csat_qst_objtitem_img 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
   AND qst_seq = #{questionSequence}
   AND objtitem_seq = #{objectItemSequence}
   AND img_seq = #{imageSequence}

mapper.delete:
DELETE FROM csat_qst_objtitem_img 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}

   		AND section_seq = #{sectionSequence}


   		AND subject_seq = #{subjectSequence}

DELETE cqoi FROM csat_qst_objtitem_img cqoi , (SELECT csat_seq,exam_seq,section_seq,subject_seq FROM csat_exam_subject WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence} AND subject_seq = #{list.subjectSequence}) )) ces WHERE cqoi.csat_seq = ces.csat_seq AND cqoi.exam_seq = ces.exam_seq AND cqoi.section_seq = ces.section_seq AND cqoi.subject_seq = ces.subject_seq

DELETE cqoi FROM csat_qst_objtitem_img cqoi , (SELECT csat_seq,exam_seq,section_seq FROM csat_exam_section WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence}) )) ces WHERE cqoi.csat_seq = ces.csat_seq AND cqoi.exam_seq = ces.exam_seq AND cqoi.section_seq = ces.section_seq
