mapper.select:
	SELECT content 
	  FROM csat_qst_text_content 
	 WHERE csat_seq = #{csatSequence}
	   AND exam_seq = #{examSequence}
	   AND section_seq = #{sectionSequence}
	   AND subject_seq = #{subjectSequence}
	   AND text_seq = #{textSequence}
  ORDER BY content_seq ASC

mapper.delete:
DELETE FROM csat_qst_text_content
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}

   		AND section_seq = #{sectionSequence}


   		AND subject_seq = #{subjectSequence}

DELETE cqtc FROM csat_qst_text_content cqtc , (SELECT csat_seq,exam_seq,section_seq,subject_seq FROM csat_exam_subject WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence} AND subject_seq = #{list.subjectSequence}) )) ces WHERE cqtc.csat_seq = ces.csat_seq AND cqtc.exam_seq = ces.exam_seq AND cqtc.section_seq = ces.section_seq AND cqtc.subject_seq = ces.subject_seq

DELETE cqtc FROM csat_qst_text_content cqtc , (SELECT csat_seq,exam_seq,section_seq FROM csat_exam_section WHERE csat_seq IN
#{list.csatSequence}
AND exam_seq IN
#{list.examSequence}
AND ! (
(section_seq = #{list.sectionSequence}) )) ces WHERE cqtc.csat_seq = ces.csat_seq AND cqtc.exam_seq = ces.exam_seq AND cqtc.section_seq = ces.section_seq
