mapper.select:
SELECT COUNT(*) 
  FROM csat_qst_text_img 
 WHERE csat_seq = #{csatSequence} 
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
   AND text_seq = #{textSequence}
   AND img_seq = #{imageSequence}

SELECT file_name 
  FROM csat_qst_text_img 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
   AND text_seq = #{textSequence}
   AND img_seq = #{imageSequence}

SELECT file_code 
  FROM csat_qst_text_img 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}
   AND section_seq = #{sectionSequence}
   AND subject_seq = #{subjectSequence}
   AND text_seq = #{textSequence}
   AND img_seq = #{imageSequence}

mapper.delete:
DELETE FROM csat_qst_text_img 
 WHERE csat_seq = #{csatSequence}
   AND exam_seq = #{examSequence}

   		AND section_seq = #{sectionSequence}


   		AND subject_seq = #{subjectSequence}
